def count_total
  handles = inputs
  handles.length
end

def wich_shorter
  handles = inputs
  handles.sort_by! {|element| element.size }
  handles[0]
end

def five_size
  handles = inputs
  handles.filter! {|element| element.length == 6 }
  handles.count
end

def start_capital
  handles = inputs
  handles.filter! {|element| element[1].match( /[[:upper:]]/ )}
  handles.count
end

def aplha_order
  handles = inputs
  handles.sort!
  handles[0..10]
end

def size_order
  handles = inputs
  handles.sort_by! {|element| element.size }
  handles[0..10]
end

def finder
  handles = inputs
  handles.index('@epenser') + 1
end

def last
  for i in 3..32
    handles = inputs
    handles.filter! {|element| element.length == (i + 1) }
    nombre = handles.count
    if nombre != 0
      puts "il y a #{nombre} handle(s) comportant #{i} caractères."
    end
  end
end

def results
  puts "il y a #{count_total} handles dans la liste"
  puts
  puts "le hande le plus court de la liste est #{wich_shorter} "
  puts 
  puts "il y a #{five_size} handles de 5 caractères"
  puts
  puts "il y a #{start_capital} handles commençant par une majuscule"
  puts
  puts "Voici le début de la liste triée dans l'ordre alphabétique: "
  puts aplha_order
  puts
  puts "Voici le début de la liste triée par taille: "
  puts size_order
  puts
  puts "@epenser est placé en position #{finder} "
  puts
  puts "Voici la répartion des handles par taille de ces derniers: "
  last
  puts
end

def perform
  results
end

perform